import React, { Component } from 'react'
import { Flex, Card, Typography, Col, Image, Rate } from 'antd'
import { format } from 'date-fns'

import MovieService from '../services/movieService'

import GenresMovie from './genresMovie'

const { Paragraph, Title } = Typography

export default class DesktopComponent extends Component {
  movieService = new MovieService()

  truncateOverview = (str) => {
    return str.length > 130 ? str.slice(0, str.indexOf(' ', 130)) + 'â€¦' : str
  }

  setRatingColor(num) {
    if (num < 3) return '#E90000'
    else if (num >= 3 && num < 5) return '#E97E00'
    else if (num >= 5 && num < 7) return '#E9D100'
    else if (num >= 7) return '#66E900'
  }

  getRating = (id) => {
    const { ratingList } = this.props
    const idx = ratingList.findIndex((el) => el.id === id)

    if (idx === -1) {
      return 0
    }

    const item = ratingList[idx]

    return item.rating
  }

  render() {
    const { movieList, sessionId, setRating } = this.props

    return (
      <>
        {movieList.map((movie) => (
          <Col key={movie.id} col={12}>
            <Card hoverable className="card">
              <Flex justify="center">
                <Image
                  alt={movie.title}
                  className=".card_image"
                  src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`}
                  width={200}
                  height={277}
                  fallback="data:image/png;base64,"
                />
                <Flex vertical className="card_info">
                  <Flex justify="space-between">
                    <Title level={movie.title.length > 20 ? 5 : 4} className="card_title">
                      {movie.title}
                    </Title>
                    <Title
                      level={5}
                      className="card_rating"
                      style={{
                        borderColor: this.setRatingColor(movie.vote_average.toFixed(1)),
                      }}
                    >
                      {movie.vote_average.toFixed(1) < 10 ? movie.vote_average.toFixed(1) : 10}
                    </Title>
                  </Flex>
                  <Paragraph className="card_date">
                    {movie.release_date ? format(new Date(movie.release_date), 'MMMM dd, yyyy') : ' '}
                  </Paragraph>
                  <Paragraph className="card_genres">
                    <GenresMovie genresId={movie.genre_ids} />
                  </Paragraph>
                  <Paragraph className="card_description">{this.truncateOverview(movie.overview)}</Paragraph>
                  <Rate
                    allowHalf
                    value={movie.rating ? movie.rating : this.getRating(movie.id)}
                    onChange={(value) => {
                      this.movieService.addRating(movie.id, sessionId, value)
                      setRating(movie.id, value)
                    }}
                    count={10}
                    className="card_rating-stars"
                  />
                </Flex>
              </Flex>
            </Card>
          </Col>
        ))}
      </>
    )
  }
}
