import React, { Component } from 'react'
import { Flex, Card, Typography, Col, Image, Rate } from 'antd'
import { format } from 'date-fns'

const { Text, Paragraph, Title } = Typography

const cardStyle = {
  width: '451px',
  height: '279px',
}
export default class MoviesView extends Component {
  truncateOverview = (str) => {
    return str.length > 130 ? str.slice(0, str.indexOf(' ', 130)) + 'â€¦' : str
  }

  setRatingColor(num) {
    if (num < 3) return '#E90000'
    else if (num >= 3 && num < 5) return '#E97E00'
    else if (num >= 5 && num < 7) return '#E9D100'
    else if (num >= 7) return '#66E900'
  }

  state = {
    ratingValue: 0,
  }

  render() {
    const { movieList, onToggleRating } = this.props
    console.log(movieList)

    return (
      <>
        {movieList.map((movie) => (
          <Col key={movie.id} span={12}>
            <Card hoverable style={cardStyle} styles={{ body: { padding: 0, overflow: 'hidden' } }}>
              <Flex justify="center">
                <Image
                  alt={movie.title}
                  src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`}
                  width={200}
                  height={277}
                  fallback="data:image/png;base64,"
                />
                <Flex vertical style={{ padding: '10px 20px', width: '70%' }}>
                  <Flex justify="space-between">
                    <Title level={4} style={{ maxWidth: '200px' }}>
                      {movie.title}
                    </Title>
                    <Title
                      level={5}
                      style={{
                        marginTop: 0,
                        border: '2px solid',
                        borderRadius: '50%',
                        width: '30px',
                        height: '30px',
                        textAlign: 'center',
                        borderColor: this.setRatingColor(movie.vote_average.toFixed(1)),
                      }}
                    >
                      {movie.vote_average.toFixed(1)}
                    </Title>
                  </Flex>
                  <Paragraph>
                    {movie.release_date ? format(new Date(movie.release_date), 'MMMM dd, yyyy') : ' '}
                  </Paragraph>
                  <Paragraph>
                    <Text code>Action</Text>
                    <Text code>Drama</Text>
                  </Paragraph>
                  <Paragraph>{this.truncateOverview(movie.overview)}</Paragraph>
                  <Rate
                    allowHalf
                    onChange={(value) => onToggleRating(movie.id, value)}
                    count={10}
                    style={{ fontSize: '16px' }}
                  />
                </Flex>
              </Flex>
            </Card>
          </Col>
        ))}
      </>
    )
  }
}
